tor [data-css=\"tve-u-15f78178728\"] { font-size: 17px !important; }[data-css=\"tve-u-15f78178728\"] { line-height: 1.75em !important; }#tve_editor [data-css=\"tve-u-15f7817ad79\"] { font-size: 17px !important; }[data-css=\"tve-u-15f7817ad79\"] { line-height: 1.75em !important; }#tve_editor [data-css=\"tve-u-15f7817b851\"] { font-size: 17px !important; }[data-css=\"tve-u-15f7817b851\"] { line-height: 1.75em !important; }#tve_editor [data-css=\"tve-u-15f7817c24f\"] { font-size: 17px !important; }[data-css=\"tve-u-15f7817c24f\"] { line-height: 1.75em !important; }#tve_editor [data-css=\"tve-u-15f7817f4d6\"] { font-size: 17px !important; }[data-css=\"tve-u-15f7817f4d6\"] { line-height: 1.75em !important; }#tve_editor [data-css=\"tve-u-15f7818014b\"] { font-size: 17px !important; }[data-css=\"tve-u-15f7818014b\"] { line-height: 1.75em !important; }#tve_editor [data-css=\"tve-u-15f781810cb\"] { font-size: 17px !important; }[data-css=\"tve-u-15f781810cb\"] { line-height: 1.75em !important; }#tve_editor [data-css=\"tve-u-15f78182061\"] { font-size: 17px !important; }[data-css=\"tve-u-15f78182061\"] { line-height: 1.75em !important; }#tve_editor [data-css=\"tve-u-15f78182b2e\"] { font-size: 17px !important; }[data-css=\"tve-u-15f78182b2e\"] { line-height: 1.75em !important; }#tve_editor [data-css=\"tve-u-15f78183804\"] { font-size: 17px !important; }[data-css=\"tve-u-15f78183804\"] { line-height: 1.75em !important; }#tve_editor [data-css=\"tve-u-15f78184c85\"] { font-size: 17px !important; }[data-css=\"tve-u-15f78184c85\"] { line-height: 1.75em !important; }}@media (max-width: 767px){[data-css=\"tve-u-15f73c67d17\"] { margin-top: 70px !important; }[data-css=\"tve-u-15f7819513b\"] { padding-top: 8px !important; margin-top: -5px !important; }[data-css=\"tve-u-15f73e87cb7\"] { width: 256px; }[data-css=\"tve-u-15f78184c85\"] { line-height: 2em !important; }[data-css=\"tve-u-15f78183804\"] { line-height: 2em !important; }[data-css=\"tve-u-15f78182b2e\"] { line-height: 2em !impor