e-height: 1.75em !important; }#tve_editor [data-css=\"tve-u-15f78184c85\"] { font-size: 17px !important; }[data-css=\"tve-u-15f78184c85\"] { line-height: 1.75em !important; }}@media (max-width: 767px){[data-css=\"tve-u-15f73c67d17\"] { margin-top: 70px !important; }[data-css=\"tve-u-15f78184c85\"] { line-height: 2em !important; }[data-css=\"tve-u-15f78183804\"] { line-height: 2em !important; }[data-css=\"tve-u-15f78182b2e\"] { line-height: 2em !important; }[data-css=\"tve-u-15f78182061\"] { line-height: 2em !important; }[data-css=\"tve-u-15f781810cb\"] { line-height: 2em !important; }[data-css=\"tve-u-15f7818014b\"] { line-height: 2em !important; }[data-css=\"tve-u-15f7817f4d6\"] { line-height: 2em !important; }[data-css=\"tve-u-15f7817c24f\"] { line-height: 2em !important; }[data-css=\"tve-u-15f7817b851\"] { line-height: 2em !important; }[data-css=\"tve-u-15f7817ad79\"] { line-height: 2em !important; }[data-css=\"tve-u-15f73d7c1d0\"] { line-height: 1.5em !important; }[data-css=\"tve-u-15f78178728\"] { line-height: 2em !important; }[data-css=\"tve-u-15f78177579\"] { line-height: 2em !important; }[data-css=\"tve-u-15f7817680a\"] { line-height: 2em !important; }[data-css=\"tve-u-15f78175e97\"] { line-height: 2em !important; }[data-css=\"tve-u-15f78175117\"] { line-height: 2em !important; }[data-css=\"tve-u-15f78173b2c\"] { line-height: 2em !important; }[data-css=\"tve-u-15f78172ae7\"] { line-height: 2em !important; }[data-css=\"tve-u-15f781705b9\"] { line-height: 2em !important; }[data-css=\"tve-u-15f780ffc14\"] { margin-left: 15px !important; padding-top: 0px !important; }#tve_editor [data-css=\"tve-u-15f781280f9\"] { font-size: 26px !important; }}'),(3891,252,'tve_revision_tve_user_custom_css',''),(3892,252,'tve_revision_tve_page_events','a:0:{}'),(3893,252,'tve_revision_tve_globals','a:2:{s:1:\"e\";s:1:\"1\";s:8:\"font_cls\";a:0:{}}'),(3894,252,'tve_revision_tve_global_scripts',''),(3895,252,'tve_revision_thrive_icon_pack','0'),(3896,252,'tve_revision_thrive_tcb_po